import React, {useState, useEffect} from 'react';

const Deconnexion = () => {

  //var d'etat
  const [deco,setDeco] = useState(false);

  useEffect(() => {
    if(deco) {
      console.log(deco);
      return (
        <li className="list-box">
          <Link
            to="/"
            onClick={e => setDeco(e.target.deco)}
            className="link-list col-3 ml-2">
            Deconnexion
          </Link>
        </li>
      )
    }
  }, [deco]);

}

export default Deconnexion;
