import React from 'react';

// Composant du diapo (abstract)
import {Slide} from 'react-slideshow-image';
//import Slide from '../diapo/Slide';

// Le Dom virtuel impose de conaître d'avance ces composants & elements.
import dia0 from '../../media/img/dia0.png';
import nodejs from '../../media/img/dia1.png';
import firefox from '../../media/img/wrapInscription.jpg';

const Images = [
    {
      src : dia0,
      txt : "Par où commencer",
      alt : "plante"
    },
    {
      src : nodejs,
      txt : "envirronement de développement",
      alt : "nodeJs"
    },
    {
      src : firefox,
      txt : "navigateur edition dev firefox",
      alt : "firefox"
    },
];

const Main = (props) => {
  return (
    <main>
      <Slide>
          {
            Images.map((img,k) => {
              <figure key={k}>
                <img src={img.src} alt=""/>
                <figcaption>aaaa</figcaption>
            </figure>
            })

          }
      </Slide>
    </main>
  )
}

export default Main;
