import React, { useRef, useState} from 'react';

const Navbar = () => {


  const [opacify,setOpacify] = useState(false);
  const refButton = useRef(null);

  //mount

  const withOpacify = () => {
    refButton.current.classList.add("opacify");

    }

  const notOpacify = () => {
    refButton.current.classList.remove("opacify");
    }



    return (
      <nav className="navbar row">
        <ul className="box-navbar col-6">
          <li className="list-box">
            <a href="" className="link-list">liens-1</a>
          </li>
          <li className="list-box">
            <a href="" className="link-list">liens-2</a>
          </li>
          <li className="list-box">
            <a href="" className="link-list">liens-3</a>
          </li>
        </ul>
        <div className="row col-6">
            <button
              onMouseOver={notOpacify}
              onMouseOut={withOpacify}
              ref={refButton}
              className="col-2 offset-7 opacify">
              Connexion
            </button>

            <button
              onMouseOver={notOpacify}
              onMouseOut={withOpacify}
              ref={refButton}
              className="col-2 ml-2 opacify">
              Inscription
            </button>
        </div>
      </nav>
    )
  }

export default Navbar;
