import app from 'firebase/app';
import 'firebase/auth';
import 'firebase/firestore';
//import key from './key/config.json';

//key de l'api :)
//const config = key;

const config = {
  apiKey: "AIzaSyBCAFvQ3T6x_yn7ZyN-YsIMh_1ZY0qKbY8",
  authDomain: "modulefront.firebaseapp.com",
  databaseURL: "https://modulefront.firebaseio.com",
  projectId: "modulefront",
  storageBucket: "modulefront.appspot.com",
  messagingSenderId: "1096124293817",
  appId: "1:1096124293817:web:753894a6eea6b021337168"
}

class Firebase {
  constructor() {
    app.initializeApp(config);
    this.auth = app.auth();
    this.db = app.firestore();
  }

  inscription = (email,password) =>
    this.auth.createUserWithEmailAndPassword(email,password)
    .catch(function(error) {
      // Handle Errors here.
      var errorCode = error.code;
      var errorMessage = error.message;
      if (errorCode == 'auth/weak-password') {
        alert('The password is too weak.');
      } else {
        alert(errorMessage);
      }
      console.log(error);
    });


  connexion = (email,password) =>
    this.auth.signInWithEmailAndPassword(email,password);

  deconnexion = () =>
    this.auth.signOut();


  user = uid =>
    this.db.doc(`user/${uid}`);
}

export default Firebase;
