import React, {Fragment,useState,useContext} from 'react';
import {Link} from 'react-router-dom';
import {FirebaseContext} from '../Firebase';

import wrap from '../../media/img/wrapInscription.jpg';
import coffe from '../../media/img/coffeW.png';


const Inscription = () => {
  //prendre le contexte
  const ctx = useContext(FirebaseContext);

  //crée les data initial
  const data = {
    pseudo : '',
    email : '',
    password : '',
    confPassword :''
  }

  //déclarée des etats
  const [dataForm, setData] = useState({data});
  const [error, setError] = useState('');
  //changer cet etat, assigner les vals
  const handleform = e => {
    setData({...dataForm,[e.target.id]:e.target.value});
  }

  const {pseudo,email,password,confPassword} = dataForm;

  //envoyée a api
  const handleSubmit = e => {
    e.preventDefault();
    const {email,password} = dataForm;
    //si envoyée ou échouer, l'etat devient l'objet initial (purge)
    ctx.inscription(email,password).then(user => {
      setData({...data});
    })
    .catch(error => {
      setError(error);
      setData({...data});
    })
  }

  //error
  const errorForm = error !== '' &&
    <div className="offset-1 col-xs-11 col-sm-11 my-2">
      {error.message}
    </div>;

  const buttonForm = pseudo === '' || email === '' || password === '' || confPassword === '' ||
  password !== confPassword ?
  <button disabled className="col-3 p-1 btn-neon shade">Suivant</button>
  :
  <button className="col-3 p-1 btn-neon">Suivant</button>

  return (
    <Fragment>
      <section className="wrap container row col-sm-12" style={{backgroundImage:`url(${wrap})`}}>
        <form action="" className="row offset-0 offset-sm-2 p-xs-2 p-sm-5 col-xs-12 col-sm-8 mt-xs-0 mt-sm-5 form inscription"
          onSubmit={handleSubmit}>
          <fieldset className="col-xs-12 col-sm-12 col-lg-8 row">
            <legend className="col-12">Profil</legend>
            <label htmlFor="pseudo" className="row my-3 col-12 col-md-10">
              <input type="text"
                id="pseudo"
                placeholder="Indiquez votre Pseudo"
                required
                autoFocus
                onChange={handleform}
                value={pseudo || ''}/>
            </label>
            <label htmlFor="email" className="row my-3 col-12 col-md-10">
              <input type="email"
                id="email"
                placeholder="Indiquez votre mail"
                required
                onChange={handleform}
                value={email || ''}/>
            </label>
            <label htmlFor="password" className="row my-3 col-12 col-md-10">
              <input type="password"
                id="password"
                placeholder="Choisissez un mot de passe"
                required
                onChange={handleform}
                value={password || ''}/>
            </label>
            <label htmlFor="confPassword" className="row my-3 col-12 col-md-10">
              <input type="password"
                id="confPassword"
                placeholder="Comfirmer le mot de passe"
                required
                onChange={handleform}
                value={confPassword || ''}/>
            </label>
            <div className="row my-3 col-12 col-md-10">
              {buttonForm}
            </div>
          </fieldset>
          <figure className=" d-none d-lg-block col-lg-4">
            <img src={coffe} alt="coffee" className="fluid"/>
          </figure>
          {errorForm}
        </form>
        <div className="row offset-2 col-8 infoSub">
            <h3 className="offset-1 col-11">Autre alternative :</h3>
            <Link className="link-list col-12" to="/connection">Se connecté</Link>
        </div>
        <section className="row offset-0 offset-sm-2 col-xs-12 col-sm-8 infoSub">
          <article className="row offset-1 col-11">
            <h3 className="col-12">A quoi sert l'inscription ?</h3>
            <p className="col-7 mb-3">
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quis labore beatae autem temporibus impedit amet excepturi dolores dignissimos molestias, dolorum?
            </p>
            <h3 className="col-12">Où sont stockée les données ?</h3>
            <p className="col-7 mb-3">
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Quis labore beatae autem temporibus impedit amet excepturi dolores dignissimos molestias, dolorum?
            </p>

          </article>
        </section>
      </section>
    </Fragment>
  )
}

export default Inscription;
