//Packages React
import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
//Packages Tiers
import {CSSTransition,TransitionGroup} from 'react-transition-group';
//Components layouts
import Header from './layouts/Header';
import Footer from './layouts/Footer';
//Components pages
import Home from './pages/Home';
import Connexion from './pages/Connexion';
import Inscription from './pages/Inscription';
import Page2 from './pages/Page2';
import NoFound from './pages/NoFound';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      depth : this.getDepth(this.props.location)
    };
  }

  getDepth(location) {
    let locationPath = location.pathname.split("/");
    //si path est != de !!, return sa longueur
    locationPath = locationPath.filter(k => k != "");
    return locationPath.length
  }

  // eq useEffect
  componentWillReceiveProps() {
    this.setState({depth:this.getDepth(this.props.location)})
  }
  render() {
    const {location} = this.props;
    const key = location.pathname.split("/")[1] || "/";
    const duration = {
      enter:1000,
      outer:500
    };

    return (
      <TransitionGroup Component="div">
        <CSSTransition
          key={key}
          timeout={duration}
          className=".switch__animate"
          mountOnEnter = {false}
          mountOnExit = {true}
        >
          <div className= {
              getDepth(location) - this.state.depth >= 0 ? "switch__animate" : "switch__animate"
            }>
            <Header />
            <Switch location={location}>
              <Route exact path ="/" component={Home}/>
              <Route path ="/connexion" component={Connexion}/>
              <Route path ="/inscription" component={Inscription}/>
              <Route path ="/page2" component={Page2}/>
              <Route component={NoFound}/>
            </Switch>
            <Footer />
          </div>
        </CSSTransition>
      </TransitionGroup>
      )
    }
  }

  export default App;
