import React, {useState, useContext, useEffect} from 'react';
import {FirebaseContext} from '../Firebase';


const Main = (props) => {

  const ctx = useContext(FirebaseContext);
  //var etats
  const [session,setSession] = useState(null);

  useEffect(() => {
    //methode propre a fireBase, check le state de session @{call}
    let instance = ctx.auth.onAuthStateChanged(user => {
      user ? setSession(user) : props.history.push("/connexion");
      });
    //componentWillUnMount ou phase de démontage pour un hook
    //d'où le context en local
    return () => {
      instance();
    }
  },[]);

  return session !== null (
    <section>
      <p>tu est co </p>
    </section>
  )

}

export default Main;
