import React, {useState, useContext, useEffect} from 'react';
import {FirebaseContext} from '../Firebase';



const Main = (props) => {

  const ctx = useContext(FirebaseContext);
  //var etats
  const [session,setSession] = useState(null);
  const [userData,setUserData] = useState({});

  useEffect(() => {

    //si session actif
    //const user = ctx.auth.currentUser;
    const user = ctx.auth.onAuthStateChanged;

    if(user) {
      setSession(user.uid);
    } else {
      setSession(null);
      props.history.push("/connexion");
    }

    if(session) {
      ctx.user(session.uid)
      .get()
      .then(doc => {
        if(doc && doc.exists) {
          setUserData(doc.data());

        }
      })
      .catch((error) => {
        console.log(`${error} : pas de collection trouver`);
      })
    }

  },[ctx,ctx.auth.currentUser, props.history,session]);

  return session === null ? (
    <main className="container row">
      <div className="helper">
        <p>..Cherche si tu existe dans les registres..</p>
      </div>
    </main>
  ) : (
    <>
      <p>Session trouv√©e ! Bienvenue {props.userData}</p>
    </>
  )
}

export default Main;
