import React from 'react';
import {Link} from 'react-router-dom';
// Composant du diapo (abstract)

//import Slide from '../diapo/Slide';

// Le Dom virtuel impose de conaître d'avance ces composants & elements.
import iconNpm from '../../media/icon/iconNpm.png';
import iconReact from '../../media/icon/iconReactNative.png';
import iconSass from '../../media/icon/iconSass.png';

const Home = props => {
  const logo = {
    src: [
      {iconNpm},
      {iconReact},
      {iconSass}
    ],
    txt : ["aaa", "bbb", "ccc"
    ],
    alt : ["ssss", "eeee", "zzzz"
    ]
  };

  return (
    <main>
      <section className="row offset-xs-0 offset-sm-3 col-xs-12 col-sm-6 py-3 my-5 xs-center infoSub">
          <h3 className="offset-sm-1 col-12 col-sm-11">Ce &#123; petit &#125; site</h3>
          <p>Sert pour le repassage de la module front</p>
          <hr/>
          <figure className="col-12">
            {
              logo.map((doc,k) => {
                <img key={k} src={doc.src} alt={doc.alt}/>
              })
            }
          </figure>
          <Link className="offset-sm-1 col-12 col-sm-11" to="/inscription">S'inscrire</Link>
      </section>
    </main>
  )
}

export default Home;
