import React, {useState, useContext, useEffect, fragment} from 'react';
import {FirebaseContext} from '../Firebase';
import Connexion from '../sas/Connexion';


const Main = (props) => {

  const ctx = useContext(FirebaseContext);
  //var etats
  const [session,setSession] = useState(null);

  useEffect(() => {
    //methode propre a fireBase, check le state de session @{call}
    let instance = ctx.auth.onAuthStateChanged(user => {
      if(user) {
        setSession(user);
      } else {
        props.history.push("/connexion");
      }
    });
    //componentWillUnMount ou phase de démontage pour un hook
    //d'où le context en local
    return () => {
      instance();
    }
  },[]);

  return session ? (
    <main className="container row">
      <div className="helper">
        Le Home (page initial)
      </div>
    </main>
  ) : (
    <>
      <p>..Cherche si tu existe dans les registres..</p>
    </>
  )
}

export default Main;
