import React from 'react';
import {Link} from 'react-router-dom';
// Composant du diapo (abstract)



// Le Dom virtuel impose de conaître d'avance ces composants & elements.
import iconNpm from '../../media/icon/iconNpm.png';
import iconReact from '../../media/icon/iconReactNative.png';
import iconSass from '../../media/icon/iconSass2.png';
import iconAssets from '../../media/icon/iconAssets.png';
import iconYarn from '../../media/icon/iconYarn.png';
import iconMongo from '../../media/icon/iconMongo.png';
import iconExpressJs from '../../media/icon/iconExpressJS.png';
import iconNodeJS from '../../media/icon/iconNodeJS.png';


const Home = () => {

  const media = {
    front : [
      {uri:iconNpm, alt:"logo Npm", txt:"gestionnaire de packages"},
      {uri:iconReact, alt:"logo ReactJs", txt:"librairie javaScript"},
      {uri:iconSass, alt:"logo Sass", txt:"préprocesseur sass"},
      {uri:iconAssets, alt:"logo html, css, javaScript", txt:"assets"},
    ],
    back : [
      {uri:iconYarn, alt:"logo Yarn", txt:"gestionnaire de packages"},
      {uri:iconMongo, alt:"logo mongo", txt:"base orientée document"},
      {uri:iconExpressJs, alt:"logo ExpressJS", txt:"micro framework"},
      {uri:iconNodeJS, alt:"logo NodeJs", txt:"env js server"}
    ]
  }

/*_________________________*/

/*_________________________*/
  const {front} = media;
  const {back} = media;



  return (
    <main>
      <section className="row offset-xs-0 offset-sm-3 col-xs-12 col-sm-6 py-3 my-sm-10 xs-center infoSub">
          <h2 className="offset-sm-1 col-12 col-sm-11">Ce &#123; petit &#125; site</h2>
          <p>Cotée Client : </p>
          <hr/>
            <figure className="col-12">
              {
                front.map((logo,k) =>
                  <img key={k} src={logo.uri} alt={logo.alt} className="col-3"/>
                )}
            </figure>
            <hr/>
          <p>Cotée Server : </p>
            <figure className="col-12">
              {
                back.map((logo,k) =>
                  <img key={k} src={logo.uri} alt={logo.alt} className="col-3"/>
              )}
            </figure>
          <Link className="offset-sm-1 col-12 col-sm-11" to="/inscription">S'inscrire</Link>
      </section>
    </main>
  )
}

export default Home;
