import React, { useRef, useState, useEffect} from 'react';

const Navbar = () => {


  const [opacify,setOpacify] = useState(false);
  const refButton1 = useRef(null);
  const refButton2 = useRef(null);

  /*useEffect( () => {
    refButton2.current.classList.add("")
  }, [])*/


  const buttonHide = () => {
    refButton1.current.classList.add("buttonShow");
    refButton2.current.classList.add("buttonShow");
  }

  const buttonShow = () => {
    refButton2.current.classList.add("buttonHide");
    refButton1.current.classList.add("buttonHide");
  }
  //mount

    return (
      <nav className="navbar row">
        <ul className="box-navbar col-6">
          <li className="list-box">
            <a href="/" className="link-list">liens-1</a>
          </li>
          <li className="list-box">
            <a href="/" className="link-list">liens-2</a>
          </li>
          <li className="list-box">
            <a href="/" className="link-list">liens-3</a>
          </li>
        </ul>
        <div className="row col-6">
            <button
              onMouseOver={buttonHide}
              onMouseOut={buttonShow}
              ref={refButton1}
              className="col-2 offset-7">
              Connexion
            </button>

            <button
              onMouseOver={buttonHide}
              onMouseOut={buttonShow}
              ref={refButton2}
              className="col-2 ml-2">
              Inscription
            </button>
        </div>
      </nav>
    )
  }

export default Navbar;
