//Packages React
import React, {Component} from 'react';
import {BrowserRouter as Router, Route, Switch} from 'react-router-dom';
//Packages Tiers
import {CSSTransition,TransitionGroup} from 'react-transition-group';
//Components layouts
import Header from './layouts/Header';
import Footer from './layouts/Footer';
//Components pages
import Home from './pages/Home';
import Connexion from './pages/Connexion';
import Inscription from './pages/Inscription';
import Page2 from './pages/Page2';
import NoFound from './pages/NoFound';

class App extends Component {
  render() {
    return (
      <Router>
        <Route render = {({location}) => (
          <>
            <Header />
            <TransitionGroup>
              <CSSTransition
                key={location.key}
                timeout={300}
                classNames="page"
                unmountOnExit
              >
                <Switch location={location}>
                  <Route exact path ="/" component={Home} activeClassName="enter"/>
                  <Route path ="/connexion" component={Connexion} activeClassName="enter"/>
                  <Route path ="/inscription" component={Inscription} activeClassName="enter"/>
                  <Route path ="/page2" component={Page2} activeClassName="enter"/>
                  <Route component={NoFound}/>
                </Switch>
              </CSSTransition>
            </TransitionGroup>
            <Footer />
          </>
      )} />
      </Router>
    )
  }
}

export default App;
