import React, { useRef} from 'react';

const Navbar = () => {

  const refButton1 = useRef(Navbar);
  const refButton2 = useRef(Navbar);
//let {b = 'valeur par défaut'} = {a: 0}
   let {button1 = refButton1.current.classList} = button1;

  const buttonLeft = () => {
    refButton2.current.classList.add("shade");
  }
  const buttonRight = () => {
    refButton1.add("shade");
  }


  const removeShade = () => {
    if(refButton1.contains("shade")) {
      setTimeout( () => {
          refButton1.remove("shade");
      },1000);
      console.log(refButton1.current.classList);
    } else if(refButton2.current.classList.contains("shade")) {
      setTimeout( () => {
          refButton2.current.classList.remove("shade");
      },1000);
    }
  }

    return (
      <nav className="navbar row">
        <ul className="box-navbar col-6">
          <li className="list-box">
            <a href="/" className="link-list">liens-1</a>
          </li>
          <li className="list-box">
            <a href="/" className="link-list">liens-2</a>
          </li>
          <li className="list-box">
            <a href="/" className="link-list">liens-3</a>
          </li>
        </ul>
        <div className="row col-6">
            <button
              onMouseOver={buttonLeft}
              onMouseOut={removeShade}
              ref={refButton1}
              className="col-2 offset-7 btn-success">
              Connexion
            </button>

            <button
              onMouseOver={buttonRight}
              onMouseOut={removeShade}
              ref={refButton2}
              className="col-2 ml-2 btn-success">
              Inscription
            </button>
        </div>
      </nav>
    )
  }

export default Navbar;
