import {Component} from 'react';

// Composant du diapo (abstract)

//import Slide from '../diapo/Slide';

// Le Dom virtuel impose de conaître d'avance ces composants & elements.
import dia1 from '../../media/img/dia0.png';
import dia2 from '../../media/img/dia0.png';
//import dia3 from '../../media/img/dia1.png';
//import dia4 from '../../media/img/wrapInscription.jpg';


class Home extends Component {

  obj = {
    txt : ["texte 1", "texte 2"],
    src : [dia1, dia2],
    alt : ["alt 1", "alt 2"]
  }

  render() {
    return (
      <main>
        <figure>
          {
            obj.map((img,k) => {
            <div key={k}>
                <img src="" alt=""/>
                <figcaption>{}</figcaption>
            </div>
          })
          }
        </figure>
      </main>
    )
  }
}

export default Home;
